import { CommonModule } from '@angular/common';
import { Component, ElementRef, ViewChild, Input } from '@angular/core';
import { FormsModule } from '@angular/forms';
import { Router } from '@angular/router';
import { UsuarioService } from '../usuario.service';

@Component({
  selector: 'app-cadastro-popup',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './cadastro-popup.component.html',
  styleUrls: ['./cadastro-popup.component.scss']
})
export class CadastroPopupComponent {
  @ViewChild('popup') popup!: ElementRef;
  @Input() popupTitle: string = 'Ação Concluída';
  @Input() popupMessage: string = 'Sua solicitação foi processada com sucesso.';

  constructor(private router: Router) {}

  openPopup(title: string, message: string) {
    this.popupTitle = title;
    this.popupMessage = message;
    this.popup.nativeElement.classList.add('show');
  }

  closePopup() {
    this.popup.nativeElement.classList.add('float-out');
    setTimeout(() => {
      this.popup.nativeElement.classList.remove('show', 'float-out');
      this.router.navigate(['/']); // Redireciona para a home após fechar o pop-up
    }, 500);
  }
}
